import React, { useState, useEffect,useRef } from "react";
import { Table, Button } from "react-bootstrap";

export default function ShowsUpdation() {
  const [availableRows, setAvailableRows] = useState(null);
  const [currentRows,setCurrentRows]=useState(null)
  const inputFile = useRef(null)
  const [thumbNails,setThumbNails]=useState({})

  function fetchTable() {
    fetch("http://localhost:3000/api/admin/getavailableshows")
      .then((res) => res.json())
      .then((data) => {
        setAvailableRows(data);
        console.log(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }
  useEffect(() => {
    fetchTable();
  }, []);


  async function onAddToShow(slot_uid) {
    console.log("add show request for:", slot_uid);
    const formData = new FormData()
    let blob = await fetch(thumbNails[slot_uid]).then(r => r.blob());
    // var fileOfBlob = new File([], 'thumbnail');
    formData.append('thumbnail',blob,slot_uid)
    fetch("http://localhost:3000/api/admin/addtoshow", {
      method: "POST",
      body: formData,
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("error:", data);
        fetchTable();
      })
      .catch((err) => {
        console.log(err);
      });
  }
  function onSetImage(e, slot_uid) {

    setThumbNails({ ...thumbNails, [slot_uid]: URL.createObjectURL(e.target.files[0]) })
    console.log(thumbNails);
  }
  return (
    <div>
      <Table bordered hover size="sm" responsive key="RC">
        <h4 style={{ textAlign: "center" }}>Available Shows</h4>
        <tbody>
          <tr>
            <th>#</th>
            <th>Email</th>
            <th>Title</th>
            <th>Description</th>
            <th>Genre</th>

            <th>Clip</th>
          </tr>

          {availableRows &&
            availableRows.map((row, ind) => (
              <tr>
                <td>{ind + 1}</td>
                <td>{row["email"]}</td>
                <td>{row["title"]}</td>
                <td>{row["note"]}</td>
                <td>{row["genre"]}</td>
                <td>
                  <audio
                    src={
                      "http://localhost:3000/audio/" +
                      row["slot_uid"] +
                      ".mp3"
                    }
                    controls
                  ></audio>
                </td>
                {/* here we can append slotid with server ip..since we know the src will be same slotid*/}
                <td>
                  <input type='file' id='file' ref={inputFile} style={{ display: 'none' }} onChange={(e)=> onSetImage(e,row['slot_uid']) } />
                  <Button varient="secondary" onClick={() => inputFile.current.click()} > add Thumbnail</Button>


                </td>
                <td>
                  <Button
                    varient="secondary"
                    onClick={()=>onAddToShow(row['slot_uid'])}
                     //here we use slot_uid to approve specific slot..but didnt shows on the table
                  >
                    Add To Show
                  </Button>
                </td>
                <td>
                  <Button
                    varient="secondary"
                    //here we use slot_uid to approve specific slot..but didnt shows on the table
                  >
                    Remove
                  </Button>
                </td>
                {thumbNails&&<td>
                  <img src={thumbNails[row["slot_uid"]]} alt="" width="350" height="200"/>
                </td>}

              </tr>
            ))}
        </tbody>
      </Table>
      <Table bordered hover size="sm" responsive key="RC">
        <h4 style={{ textAlign: "center" }}>Available Shows</h4>
        <tbody>
          <tr>
            <th>#</th>
            <th>Email</th>
            <th>Title</th>
            <th>Description</th>
            <th>Genre</th>

            <th>Clip</th>
          </tr>

          {availableRows &&
            availableRows.map((row, ind) => (
              <tr>
                <td>{ind + 1}</td>
                <td>{row["email"]}</td>
                <td>{row["title"]}</td>
                <td>{row["note"]}</td>
                <td>{row["genre"]}</td>
                <td>
                  <audio
                    src={
                      "http://localhost:3000/audio/" +
                      row["slot_uid"] +
                      ".mp3"
                    }
                    controls
                  ></audio>
                </td>
                {/* here we can append slotid with server ip..since we know the src will be same slotid*/}
                <td>
                  <input type='file' id='file' ref={inputFile} style={{ display: 'none' }} onChange={(e)=> onSetImage(e,row['slot_uid']) } />
                  <Button varient="secondary" onClick={() => inputFile.current.click()} > add Thumbnail</Button>


                </td>
                <td>
                  <Button
                    varient="secondary"
                    onClick={()=>onAddToShow(row['slot_uid'])}
                     //here we use slot_uid to approve specific slot..but didnt shows on the table
                  >
                    Add To Show
                  </Button>
                </td>
                <td>
                  <Button
                    varient="secondary"
                    //here we use slot_uid to approve specific slot..but didnt shows on the table
                  >
                    Remove
                  </Button>
                </td>
                {thumbNails&&<td>
                  <img src={thumbNails[row["slot_uid"]]} alt="" width="350" height="200"/>
                </td>}

              </tr>
            ))}
        </tbody>
      </Table>

    </div>
  );
}
