import React, { useState, useEffect,useRef } from "react";
import { Table, Button } from "react-bootstrap";

export default function ShowsUpdation() {
    const [availableRows, setAvailableRows] = useState(null);
  const [currentRows, setCurrentRows] = useState(null)
  const inputFile = useRef(null)
  const [thumbNails,setThumbNails]=useState({1:true})

  function fetchTable() {
    fetch("http://localhost:3000/api/admin/getavailableshows")
      .then((res) => res.json())
      .then((data) => {
        setAvailableRows(data);
        console.log(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }
  useEffect(() => {
    fetchTable();
  }, []);


  function approve(slot_uid) {
    console.log("approval request for", slot_uid);

    fetch("http://localhost:3000/api/admin/nonliveapprove", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ slot_uid }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("error:", data);
        fetchTable();
      })
      .catch((err) => {
        console.log(err);
      });
  }
  function onSetImage(e, slot_uid) {
    setThumbNails(Object.assign({rslot_uid:URL.createObjectURL(e.target.files[0])}))
  }
  return (
    <div>
      <Table bordered hover size="sm" responsive key="RC">
        <h4 style={{ textAlign: "center" }}>Available Shows</h4>
        <tbody>
          <tr>
            <th>#</th>
            <th>Email</th>
            <th>Title</th>
            <th>Description</th>
            <th>Genre</th>

            <th>Clip</th>
          </tr>

          {availableRows &&
            availableRows.map((row, ind) => (
              <tr>
                <td>{ind + 1}</td>
                <td>{row["email"]}</td>
                <td>{row["title"]}</td>
                <td>{row["note"]}</td>
                <td>{row["genre"]}</td>
                <td>
                  <audio
                    src={
                      "http://localhost:3000/audio/" +
                      row["slot_uid"] +
                      ".mp3"
                    }
                    controls
                  ></audio>
                </td>
                {/* here we can append slotid with server ip..since we know the src will be same slotid*/}
                <td>
                  <input type='file' id='file' ref={inputFile} style={{ display: 'none' }} onChange={(e)=> onSetImage(e,row['slot_uid']) } />
                  <Button varient="secondary" onClick={() => inputFile.current.click()} > add Thumbnail</Button>


                </td>
                <td>
                  <Button
                    varient="secondary"
                     //here we use slot_uid to approve specific slot..but didnt shows on the table
                  >
                    Add To Show
                  </Button>
                </td>
                <td>
                  <Button
                    varient="secondary"
                    //here we use slot_uid to approve specific slot..but didnt shows on the table
                  >
                    Remove
                  </Button>
                </td>
                {thumbNails&&<td>
                  <img src={thumbNails} alt="" width="350" height="200"/>
                </td>}

              </tr>
            ))}
        </tbody>
      </Table>

    </div>
  );
}
